# using chainlist.org to find the best RPC url
_eth_rpc = 'https://eth-mainnet.public.blastapi.io'
_web3 = Web3(Web3.HTTPProvider(_eth_rpc))
_xen_contract = _web3.eth.contract(address=_xen_eth_addr, abi=_xen_eth_abi)


def get_global_rank():
    return _xen_contract.functions.globalRank().call()


def get_mint_info(addr):
    return _xen_contract.functions.userMints(addr).call()


def write_info_to_file(info_data, info_csv_path):
    with open(info_csv_path, "w", newline='') as csvfile:
        writer = csv.writer(csvfile)
        writer.writerow(['No.', 'Address', 'Term', 'Maturity', 'Xen Count'])
        writer.writerows(info_data)


def local_time(timestamp):
    time_local = time.localtime(timestamp)
    dt = time.strftime("%Y-%m-%d %H:%M:%S", time_local)
    return dt


def cal_estimated_xen(term, global_rank, rank, amp, eaa):
    return term * math.log2(global_rank - rank) * amp * (1 + eaa / 1000)


def update_all_mints(csv_path, info_csv_path):
    info_datas = []
    global_rank = get_global_rank()
    with open(csv_path) as f:
        f_csv = csv.reader(f)
        headers = next(f_csv)
        row_no = 1
        for each_wallet in f_csv:
            handle_each_wallet(each_wallet, info_datas, global_rank, row_no)
            row_no += 1

    write_info_to_file(info_datas, info_csv_path)


def handle_each_wallet(each_wallet, info_datas, global_rank, row_no):
    addr = each_wallet[1]
    mint_info = get_mint_info(addr)
    term = mint_info[1]
    maturityTs = mint_info[2]
    rank = mint_info[3]
    amp = mint_info[4]
    eaa = mint_info[5]

    if rank == 0:
        return
    xen_count = cal_estimated_xen(term, global_rank, rank, amp, eaa)
    mint_info_data = [row_no, addr, term, local_time(maturityTs), int(xen_count)]
    print(mint_info_data)
    info_datas.append(mint_info_data)
